// Appointments.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AppointmentCard from './Appointmentcard.js';
import './Appointment.css';

const Appointments = () => {
    const [appointments, setAppointments] = useState([]);
    const [newAppointment, setNewAppointment] = useState({
        patientName: '',
        doctorName: '',
        date: ''
    });
    const [selectedAppointment, setSelectedAppointment] = useState(null);
    const [isEditMode, setIsEditMode] = useState(false);

    // Fetch all appointments
    useEffect(() => {
        fetchAppointments();
    }, []);

    const fetchAppointments = async () => {
        try {
            const { data } = await axios.get('http://localhost:5000/appointments');
            setAppointments(data);
        } catch (error) {
            console.error('Error fetching appointments:', error);
        }
    };

    // Add appointment
    const handleAddAppointment = async (e) => {
        e.preventDefault();
        try {
            const { data } = await axios.post('http://localhost:5000/appointments/add', newAppointment);
            setAppointments([...appointments, data]);
            setNewAppointment({ patientName: '', doctorName: '', date: '' });
        } catch (error) {
            console.error('Error adding appointment:', error);
        }
    };

    // Update appointment
    const handleUpdateAppointment = async (id, e) => {
        e.preventDefault();
        try {
            const { data } = await axios.put(
                `http://localhost:5000/appointments/update/${id}`,
                selectedAppointment
            );
            setAppointments(
                appointments.map((appt) => (appt._id === id ? data : appt))
            );
            setSelectedAppointment(null);
            setIsEditMode(false);
        } catch (error) {
            console.error('Error updating appointment:', error);
        }
    };

    // Delete appointment
    const handleDeleteAppointment = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/appointments/delete/${id}`);
            setAppointments(appointments.filter((appt) => appt._id !== id));
        } catch (error) {
            console.error('Error deleting appointment:', error);
        }
    };

    // Switch to edit mode
    const handleEditAppointment = (appointment) => {
        setSelectedAppointment(appointment);
        setIsEditMode(true);
    };

    return (
        <div className="flex-row" style={{ width: '100%' }}>
            <div className="flex-column">
                <div className="add-form">
                    <h4>{isEditMode ? 'Edit Appointment' : 'Add New Appointment'}</h4>
                    <form
                        className="appointment-form"
                        onSubmit={
                            isEditMode
                                ? (e) => handleUpdateAppointment(selectedAppointment._id, e)
                                : handleAddAppointment
                        }
                    >
                        <label>Patient Name:</label>
                        <input
                            type="text"
                            value={
                                isEditMode
                                    ? selectedAppointment.patientName
                                    : newAppointment.patientName
                            }
                            onChange={(e) =>
                                isEditMode
                                    ? setSelectedAppointment({
                                          ...selectedAppointment,
                                          patientName: e.target.value
                                      })
                                    : setNewAppointment({
                                          ...newAppointment,
                                          patientName: e.target.value
                                      })
                            }
                        />
                        <label>Doctor Name:</label>
                        <input
                            type="text"
                            value={
                                isEditMode
                                    ? selectedAppointment.doctorName
                                    : newAppointment.doctorName
                            }
                            onChange={(e) =>
                                isEditMode
                                    ? setSelectedAppointment({
                                          ...selectedAppointment,
                                          doctorName: e.target.value
                                      })
                                    : setNewAppointment({
                                          ...newAppointment,
                                          doctorName: e.target.value
                                      })
                            }
                        />
                        <label>Date:</label>
                        <input
                            type="date"
                            value={
                                isEditMode
                                    ? selectedAppointment.date?.split('T')[0]
                                    : newAppointment.date
                            }
                            onChange={(e) =>
                                isEditMode
                                    ? setSelectedAppointment({
                                          ...selectedAppointment,
                                          date: e.target.value
                                      })
                                    : setNewAppointment({
                                          ...newAppointment,
                                          date: e.target.value
                                      })
                            }
                        />
                        <button type="submit">
                            {isEditMode ? 'Update Appointment' : 'Add Appointment'}
                        </button>
                    </form>
                </div>
            </div>

            <div className="appointments">
                <h3>Appointments ({appointments.length})</h3>
                <div className="appointment-list">
                    {appointments.map((appointment) => (
                        <AppointmentCard
                            key={appointment._id}
                            appointment={appointment}
                            onEdit={handleEditAppointment}
                            onDelete={handleDeleteAppointment}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Appointments;
